import colors from "../../helpers/colors"

const styles = () => ({
    containerIconActive: {
        backgroundColor: colors.white,
        width: 400,
        height: '100%',
        // borderRadius: 32,
        display: 'flex',
        // justifyContent: 'center',
        position: 'fixed',
        top: 0,
        right: 0,
        transition: 'width 0.2s ease-out',
        ':hover': {
            backgroundColor: colors.white,
            cursor: 'pointer'
        },
    },
    containerArea: {
        width: '100%',
        height: '100%',
        // backgroundColor: colors.black,
        paddingBlock: 8,
        paddingBlock: 24,
        display: 'flex',
        flexDirection: 'column',
        // justifyContent: 'center'
    },
    containerSearch: {
        width: 272,
        height: 16,
        borderRadius: 6,
        boxShadow: '0 2px 10px #00000010',
        backgroundColor: colors.white,
        display: 'flex',
        padding: 16
        // justifyContent: 'center'
    },
    containerIcons: {

    },
    imgUserIcn: {
        height: 32,
        width: 32,
        borderRadius: 16
    },
    imgSearch: {
        height: 16,
        width: 16,
        alignSelf: 'center',
        filter: 'brightness(10) grayscale(100%)',
    },  
    imgIconActive: {
        height: 60,
        width: 60,
        borderRadius: 32,
        left: -32,
        top: 17,
        position: 'absolute',
        border: '2px solid #FFFFFF',
        // alignSelf: 'center'
    },
    containerIcon: {
        backgroundColor: colors.red2,
        width: 64,
        height: 64,
        borderRadius: 32,
        display: 'flex',
        justifyContent: 'center',
        ':hover': {
            backgroundColor: colors.white,
            cursor: 'pointer'
        },
    },
    imgIcon: {
        height: 60,
        width: 60,
        borderRadius: 32,
        alignSelf: 'center'
    },
})

export default styles
